import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';
import { Adocao } from 'src/app/Model/Adocao'
import { AdocoesService } from 'src/app/Servico/AdocaoServico/adocoes.service'; 


@Component({
  selector: 'app-lista-adocoes',
  templateUrl: './lista-adocoes.component.html',
  styleUrls: ['./lista-adocoes.component.css']
})
export class ListaAdocoesComponent {

  formulario: any;
  tituloFormulario: string = '';
  adocoes: Adocao[] = [];
  modalRef!: BsModalRef;

  constructor(
    private adocoesService: AdocoesService,
    private router: Router,
    private modalService: BsModalService
  ) {}

  ngOnInit(): void {
    this.obterAdocoes();
  }

  obterAdocoes(): void {
    this.adocoesService.listar().subscribe(
      (adocoes) => {
        this.adocoes = adocoes;
      },
      (error) => {
        console.error('Erro ao obter adoção', error);

      }
    );
  }

  removerAdocao(codigo: number): void {
    this.adocoesService.excluir(codigo).subscribe(
      () => {
        console.log('Adoção removido com sucesso!');
        this.obterAdocoes();
      },
      (error) => {
        console.error('Erro ao remover registro', error);
      }
    );
  }

  abrirModalEditCliente(cliente: Cliente): void {
    if (cliente) {
      const initialState = { cliente: { ...cliente } };

      this.modalRef = this.modalService.show(EditclienmodalComponent, {
        initialState,
      });

      if (this.modalRef.content?.Atualizado) {
        this.modalRef.content.Atualizado.subscribe(() => {
          this.obterClientes();

          this.modalRef.hide();
        });
      }
    }
  }


  formatarCpf(cpf: string): string {
    return cpf.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  }






}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Document</title>
    <link rel="stylesheet" href="lista-adocoes.component.css" />
    <link
      rel="stylesheet"
      href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.2/css/all.min.css"
      integrity="sha512-z3gLpd7yknf1YoNbCzqRKc4qyor8gaKU1qmn+CShxbuBusANI9QpRohGBreCFkKxLhei6S9CQXFEbbKuqLg0DA=="
      crossorigin="anonymous"
      referrerpolicy="no-referrer"
    />
  </head>
  <body class="container">
    <nav><button id="btn-voltar" onclick="history.back()">Voltar</button></nav>
    <h1>Lista de Adoções</h1>

    <div class="div_content">
      <div class="tab-content">
        <table>
          <tr>
            <th class="th">Codigo</th>
            <th class="th1">CPF do cliente</th>
            <th class="th2">Nome do cliente</th>
            <th class="th3">Nome do animal</th>
            <th class="th4">Data da adoção</th>
            <th class="th5">Atualizar</th>
            <th class="th6">Remover</th>
          </tr>
          <tr class="tr-cit" *ngFor="let adocao of adocoes">
            <td>{{ adocao.codigo }}</td>
            <td>{{ adocao.cliente?.cpf }}</td>
            <td>{{ adocao.cliente?.nome }}</td>
            <td>{{ adocao.animal?.nome }}</td>
            <td>{{ adocao.data }}</td>
            <td>
              <i
                id="btn-update"
                class="fa-regular fa-pen-to-square fa-2xl"

              ></i>
            </td>
            <td>
              <i
                id="btn-remove"
                class="fa-solid fa-trash-can fa-2xl"
                (click)="removerAdocao(adocao.codigo)"
              ></i>
            </td>
          </tr>
        </table>
      </div>
    </div>
  </body>
</html>
