import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { FornecedoresService } from 'src/app/Servico/FornecedorServico/fornecedores.service';
import { Fornecedor } from 'src/app/Model/Fornecedor';
import { Router } from '@angular/router';

@Component({
  selector: 'app-fornecedores',
  templateUrl: './fornecedores.component.html',
  styleUrls: ['./fornecedores.component.css'],
})
export class FornecedorComponent implements OnInit {
  formularioFornecedor: any;
  tituloFormulario: string = '';
  constructor(
    private fornecedorService: FornecedoresService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.tituloFormulario = 'Novo Fornecedor';
    this.formularioFornecedor = new FormGroup({
      cnpj: new FormControl(null),
      nome: new FormControl(null),
    });
  }

  salvarFornecedor(): void {
    const fornecedor: Fornecedor = this.formularioFornecedor.value;
    this.fornecedorService.cadastrar(fornecedor).subscribe((result) => {
      alert('Fornecedor inserido com sucesso.');
      this.router.navigate(['/home']);
    });
  }

  formatarCnpjCadastro(cnpj: string): string {
    return cnpj.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  }

}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Document</title>
    <link rel="stylesheet" href="fornecedores.component.css" />
  </head>
  <body class="container">
    <div
      class="container-xl mx-auto align-content-xl-center justify-content-center justify-content-md-around"
    >
      <div class="row">
        <div
          class="col-6 border border-light rounded p-5 shadow mx-auto"
          *ngIf="formularioFornecedor"
        >
          <h1>{{ tituloFormulario }}</h1>
          <form
            [formGroup]="formularioFornecedor"
            (ngSubmit)="salvarFornecedor()"
          >
            <div class="form-group" style="margin-top: 30px">
              <label>CNPJ:</label>
              <input
                type="text"
                class="form-control form-control-sm"
                formControlName="cnpj"
                maxlength="14"
                [value]="formatarCnpjCadastro(formularioFornecedor.get('cnpj').value)"
              />
            </div>
            <div class="form-group">
              <label>Nome:</label>
              <input
                type="text"
                class="form-control form-control-sm"
                formControlName="nome"
              />
            </div>

            <div class="div-buttons">
              <div>
                <button
                  id="btn-salvar"
                  type="submit"
                  class="btn btn-sm btn-outline-secondary"
                >
                  Salvar
                </button>
              </div>
              <div>
                <button
                  id="btn-voltar"
                  type="button"
                  class="btn btn-sm btn-light"
                  onclick="history.back()"
                >
                  Voltar
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  </body>
</html>
