import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Fornecedor } from 'src/app/Model/Fornecedor'; 
import { Produto } from 'src/app/Model/Produto'; 
import { FornecedoresService } from 'src/app/Servico/FornecedorServico/fornecedores.service'; 

@Component({
  selector: 'app-produtos',
  templateUrl: './produtos.component.html',
  styleUrls: ['./produtos.component.css'],
})
export class ProdutosComponent implements OnInit {
  formulario_produto!: FormGroup;
  fornecedores: Fornecedor[] = [];

  constructor(private formBuilder: FormBuilder,private fornecedorService: FornecedoresService) {}

  ngOnInit(): void {
    this.formulario_produto = this.formBuilder.group({
      fornecedor: ['', Validators.required],
      nome: ['', Validators.required],
      descricao: ['', Validators.required],
      valor: ['', Validators.required],
    });

    this.obterFornecedores(); // Chame o método para obter fornecedores ao inicializar o componente
  }

  obterFornecedores(): void {
    this.fornecedorService.listar().subscribe(
      (fornecedores) => {
        this.fornecedores = fornecedores;

      },
      (error) => {
        console.error('Erro ao obter fornecedores:', error);
        // Lide com o erro de acordo com a sua necessidade
      }
    );
  }

  enviarFormulario(): void {
    if (this.formulario_produto.valid) {
      const produto: Produto = this.formulario_produto.value;
      console.log(produto);
      alert("Produto "+ produto.nome +  " adicionado com sucesso" );
      // Adicione aqui a lógica para enviar o produto para o servidor, por exemplo, por meio de um serviço
    }
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Document</title>
    <link rel="stylesheet" href="produtos.component.css">
</head>
<body class="container">
    <div class="container p-5">
        <div class="row">
            <div class="col-6 border border-light rounded p-5 shadow" >

                <form [formGroup]="formulario_produto" *ngIf="formulario_produto" (ngSubmit)="enviarFormulario()">
                    <div class="form-group">
                        <label>Fornecedor</label>
                        <select class="form-control form-control-sm" formControlName="fornecedor">
                            <option *ngFor="let fornecedor of fornecedores " [value]="fornecedor.cnpj">
                              {{ fornecedor.nome }}
                            </option>
                          </select>


                    </div>

                    <div class="form-group">
                        <label>Nome</label>
                        <input type="text" class="form-control form-control-sm" formControlName="nome"/>
                    </div>
                    <div class="form-group">
                        <label>Descrição</label>
                        <input type="text" class="form-control form-control-sm" formControlName="descricao"/>
                    </div>
                    <div class="form-group">
                        <label>Valor</label>
                        <input type="text" class="form-control form-control-sm" formControlName="valor"/>
                    </div>    
                    <button type="submit" class="btn btn-primary">Enviar</button>
                </form>
            </div>
        </div>
    </div>
</body>
</html>
