import { Component } from '@angular/core';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';
import { Subject } from 'rxjs';
import { Produto } from 'src/app/Model/Produto';
import { FornecedoresService } from 'src/app/Servico/FornecedorServico/fornecedores.service';
import { ProdutosService } from 'src/app/Servico/ProdutoServico/produtos.service';

@Component({
  selector: 'app-editprodumodal',
  templateUrl: './editprodumodal.component.html',
  styleUrls: ['./editprodumodal.component.css']
})
export class EditprodumodalComponent {
  modalRef!: BsModalRef;

  produto = new Produto();
  produAtualizado: Subject<void> = new Subject<void>();

  fornecedores: Fornecedor[] = [];

  constructor(
    private modalService: BsModalService,
    private edicaoProdutoService: ProdutosService,
    private fornecedorService: FornecedoresService
  ) {}

  ngOnInit(): void {
    this.obterFornecedores();
  }

  obterFornecedores(): void {
    this.fornecedorService.listar().subscribe(
      (fornecedores) => {
        this.fornecedores = fornecedores;
      },
      (error) => {
        console.error('Erro ao obter fornecedores', error);
      }
    );
  }

  salvarEdicaoProduto(): void {
    this.edicaoProdutoService.alterar(this.produto).subscribe(
      () => {
        this.produAtualizado.next();
      },
      (error) => {
        console.log(this.produto);
        console.error('Erro ao atualizar produto', error);
      }
    );
  }
  fecharModalProduto(): void {
    this.modalService.hide();
  }
}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Editar produto</title>
    <link
      rel="stylesheet"
      href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.2/css/all.min.css"
      integrity="sha512-z3gLpd7yknf1YoNbCzqRKc4qyor8gaKU1qmn+CShxbuBusANI9QpRohGBreCFkKxLhei6S9CQXFEbbKuqLg0DA=="
      crossorigin="anonymous"
      referrerpolicy="no-referrer"
    />
  </head>
  <body class="container">
    <div class="modal-header">
      <h4 class="modal-title">Editar produto</h4>
      <button type="button" class="close" (click)="fecharModalProduto()">
        &times;
      </button>
    </div>
    <div class="modal-body">
      <div class="form-group">
        <label for="codigo">Codigo:</label>
        <input
          type="text"
          class="form-control"
          id="codigo"
          [(ngModel)]="produto.codigo"
          [attr.readonly]="true"
        />
      </div>
      <div class="form-group">
        <label for="nome">CNPJ:</label>
        <input
          type="text"
          class="form-control"
          id="nome"
          [(ngModel)]="produto.fornecedorCNPJ"
        />
      </div>
      <div class="form-group">
        <label for="nome">Nome:</label>
        <input
          type="text"
          class="form-control"
          id="nome"
          [(ngModel)]="produto.nome"
        />
      </div>
    </div>

    <div class="modal-footer">
      <button
        type="button"
        class="btn btn-primary"
        (click)="salvarEdicaoProduto()"
      >
        Salvar
      </button>
      <button
        type="button"
        class="btn btn-secondary"
        (click)="fecharModalProduto()"
      >
        Fechar
      </button>
    </div>
  </body>
</html>
