import { Component } from '@angular/core';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';
import { Subject } from 'rxjs';
import { Animal } from 'src/app/Model/Animal';
import { Cliente } from 'src/app/Model/Cliente';
import { Adocao } from 'src/app/Model/Adocao';
import { AdocoesService } from 'src/app/Servico/AdocaoServico/adocoes.service';
import { ClientesService } from 'src/app/Servico/ClienteServico/clientes.service';
import { AnimaisService } from 'src/app/Servico/AnimalServico/animais.service';




@Component({
  selector: 'app-editadocaomodal',
  templateUrl: './editadocaomodal.component.html',
  styleUrls: ['./editadocaomodal.component.css']
})
export class EditadocaomodalComponent {
  modalRef!: BsModalRef;

  adocao = new Adocao();
  Atualizado: Subject<void> = new Subject<void>();

  clientes: Cliente[] = [];
  animais: Animal[] = [];

  constructor(
    private modalService: BsModalService,
    private edicaoAdocaoService: AdocoesService,
    private clienteService: ClientesService,
    private animalService: AnimaisService
  ) {}

  ngOnInit(): void {
    this.obterClientes();
    this.obterAnimais();
  }
  obterClientes(): void {
    this.clienteService.listar().subscribe(
      (clientes) => {
        this.clientes = clientes;
      },
      (error) => {
        console.error('Erro ao obter clientes', error);
      }
    );
  }
  obterAnimais(): void {
    this.animalService.listar().subscribe(
      (animais) => {
        this.animais = animais;
      },
      (error) => {
        console.error('Erro ao obter animais', error);
      }
    );
  }
  salvarEdicaoAdocao(): void {
    this.edicaoAdocaoService.alterar(this.adocao).subscribe(
      () => {
        this.Atualizado.next();
      },
      (error) => {
        console.log(this.adocao);
        console.error('Erro ao atualizar adocao', error);
      }
    );
  }
  fecharModalAdocao(): void {
    this.modalService.hide();
  }

}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Editar Adoção</title>
    <link
      rel="stylesheet"
      href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.2/css/all.min.css"
      integrity="sha512-z3gLpd7yknf1YoNbCzqRKc4qyor8gaKU1qmn+CShxbuBusANI9QpRohGBreCFkKxLhei6S9CQXFEbbKuqLg0DA=="
      crossorigin="anonymous"
      referrerpolicy="no-referrer"
    />
  </head>
  <body class="container">
    <div class="modal-header">
      <h4 class="modal-title">Editar Adoção</h4>
      <button type="button" class="close" (click)="fecharModalAdocao()">
        &times;
      </button>
    </div>
    <div class="modal-body">
      <div class="form-group">
        <label for="codigo">Codigo:</label>
        <input
          type="text"
          class="form-control"
          id="codigo"
          [(ngModel)]="adocao.codigo"
          [attr.readonly]="true"
        />
      </div>
      <div class="form-group">
        <label for="cliente">Cliente:</label>
        <select class="form-control" id="cliente" [(ngModel)]="adocao.clienteCPF">
          <option *ngFor="let cliente of clientes" [value]="cliente.cpf">
            {{ cliente.nome }}
          </option>
        </select>
      </div>





      <div class="form-group">
        <label for="data">Data:</label>
        <input
          type="text"
          class="form-control"
          id="data"
          [(ngModel)]="adocao.data"
        />
      </div>

    </div>

    <div class="modal-footer">
      <button
        type="button"
        class="btn btn-primary"
        (click)="salvarEdicaoAdocao()"
      >
        Salvar
      </button>
      <button
        type="button"
        class="btn btn-secondary"
        (click)="fecharModalAdocao()"
      >
        Fechar
      </button>
    </div>
  </body>
</html>
