import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Fornecedor } from 'src/app/Model/Fornecedor';
import { Produto } from 'src/app/Model/Produto';
import { FornecedoresService } from 'src/app/Servico/FornecedorServico/fornecedores.service';
import { ProdutosService } from 'src/app/Servico/ProdutoServico/produtos.service';
import { Observer } from 'rxjs';

@Component({
  selector: 'app-produtos',
  templateUrl: './produtos.component.html',
  styleUrls: ['./produtos.component.css'],
})
export class ProdutosComponent implements OnInit {
  formulario_produto!: FormGroup;
  tituloFormulario: string = '';
  fornecedores: Array<Fornecedor> | undefined;

  constructor(
    private fb: FormBuilder,
    private produtoService: ProdutosService,
    private fornecedorService: FornecedoresService
  ) {}

  ngOnInit(): void {
    this.tituloFormulario = 'Novo Modelo';
    this.formulario_produto = this.fb.group({
      codigo: [null],
      nome: [null],
      descricao: [null],
      valor: [null],
      fornecedor: [null, Validators.required],  // Certifique-se de que o fornecedor seja do tipo Fornecedor
    });

    this.fornecedorService.listar().subscribe(fornecedores => {
      this.fornecedores  = fornecedores ;

      if (this.fornecedores && this.fornecedores.length > 0) {
        this.formulario_produto.get('fornecedor')?.setValue(this.fornecedores[0]);
      }
    });
  }

  enviarFormulario(): void {
    if (this.formulario_produto.valid) {
      const produto: Produto = this.formulario_produto.value;

      // Certifique-se de que o fornecedor seja uma instância de Fornecedor e tem CNPJ
      if (produto.fornecedor instanceof Fornecedor && produto.fornecedor.cnpj) {
        const observer: Observer<Produto> = {
          next(_result): void {
            alert('Modelo salvo com sucesso.');
          },
          error(_error): void {
            alert('Erro ao salvar!');
          },
          complete(): void {
          },
        };

        if (produto.codigo && !isNaN(Number(produto.codigo))) {
          this.produtoService.alterar(produto).subscribe(observer);
        } else {
          this.produtoService.cadastrarProduto(produto).subscribe(observer);
        }

  }
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Document</title>
    <link rel="stylesheet" href="produtos.component.css">
</head>
<body class="container">
    <div class="container p-5">
        <div class="row">
            <div class="col-6 border border-light rounded p-5 shadow" >
            <h6>{{tituloFormulario}}</h6>
                <form [formGroup]="formulario_produto" *ngIf="formulario_produto" (ngSubmit)="enviarFormulario()">
                    <div class="form-group">
                        <label>Fornecedor</label>
                        <select class="form-control form-control-sm" formControlName="fornecedor">
                            <option *ngFor="let fornecedor of fornecedores " [value]="fornecedor.cnpj">
                              {{ fornecedor.nome }}
                            </option>
                          </select>


                    </div>

                    <div class="form-group">
                        <label>Nome</label>
                        <input type="text" class="form-control form-control-sm" formControlName="nome"/>
                    </div>
                    <div class="form-group">
                        <label>Descrição</label>
                        <input type="text" class="form-control form-control-sm" formControlName="descricao"/>
                    </div>
                    <div class="form-group">
                        <label>Valor</label>
                        <input type="text" class="form-control form-control-sm" formControlName="valor"/>
                    </div>    
                    <button type="submit" class="btn btn-primary">Enviar</button>
                </form>
            </div>
        </div>
    </div>

</body>
</html>
