import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Produto } from 'src/app/Model/Produto';
import { ProdutosService } from 'src/app/Servico/ProdutoServico/produtos.service';

@Component({
  selector: 'app-lista-produtos',
  templateUrl: './lista-produtos.component.html',
  styleUrls: ['./lista-produtos.component.css'],
})
export class ListaProdutosComponent implements OnInit {
  formulario: any;
  tituloFormulario: string = '';
  produtos: Produto[] = [];

  constructor(
    private produtoServico: ProdutosService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.obterProdutos();
  }

  obterProdutos(): void {
    this.produtoServico.listar().subscribe(
      (produtos) => {
        this.produtos = produtos;
      },
      (error) => {
        console.error('Erro ao obter produto', error);
      }
    );
  }
  formatarCnpj(cnpj: string): string {
    return cnpj.replace(
      /(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/,
      '$1.$2.$3/$4-$5'
    );
  }
}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Document</title>
    <link rel="stylesheet" href="lista-produtos.component.css" />
  </head>
  <body class="container">
    <nav><button id="btn-voltar" onclick="history.back()">Voltar</button></nav>
    <h1>Lista de Produtos</h1>

    <div class="div_content">
      <div class="tab-content">
        <table>
          <tr>
            <th class="th">Codigo</th>
            <th class="th1">CNPJ do Fornecedor</th>
            <th class="th2">Nome do Produto</th>
            <th class="th3">Descrição do Produto</th>
            <th class="th4">Valor do Produto</th>
          </tr>
          <tr class="tr-cit" *ngFor="let produto of produtos">
            <td>{{ produto.codigo }}</td>
            <td>{{ formatarCnpj(produto.fornecedorCNPJ) }}</td>
            <td>{{ produto.nome }}</td>
            <td>{{ produto.descricao }}</td>
            <td>{{ produto.valor }}</td>
            <td>
              <i
                id="btn-update"
                class="fa-regular fa-pen-to-square fa-2xl"

              ></i>
            </td>
            <td>
              <i
                id="btn-remove"
                class="fa-solid fa-trash-can fa-2xl"

              ></i>
            </td>
          </tr>
        </table>
      </div>
    </div>
  </body>
</html>
