import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Fornecedor } from 'src/app/Model/Fornecedor'; 
import { Produto } from 'src/app/Model/Produto'; 
import { FornecedoresService } from 'src/app/Servico/FornecedorServico/fornecedores.service'; 
import { Router } from '@angular/router';
import { ProdutosService } from 'src/app/Servico/ProdutoServico/produtos.service';

@Component({
  selector: 'app-produtos',
  templateUrl: './produtos.component.html',
  styleUrls: ['./produtos.component.css'],
})
// ... (seu código existente)

export class ProdutosComponent implements OnInit {
  formulario_produto!: FormGroup;
  fornecedores: Fornecedor[] = [];

  constructor(
    private formBuilder: FormBuilder,
    private fornecedorService: FornecedoresService,
    private router: Router,
    private produtoService: ProdutosService
  ) {}

  ngOnInit(): void {
    this.obterFornecedores();
    this.inicializarFormularioProduto();
  }

  inicializarFormularioProduto(): void {
    this.formulario_produto = this.formBuilder.group({
      fornecedor: [null, Validators.required],
      nome: [null, Validators.required],
      descricao: [null, Validators.required],
      valor: [null, Validators.required],
    });
  }

  obterFornecedores(): void {
    this.fornecedorService.listar().subscribe(
      (fornecedores) => {
        this.fornecedores = fornecedores;
      },
      (error) => {
        console.error('Erro ao obter fornecedores:', error);
      }
    );
  }

  salvarProduto(): void {
    const fornecedorSelecionado: Fornecedor | null = this.formulario_produto.get('fornecedor')?.value;

    if (fornecedorSelecionado) {
      const produto: Produto = {
        fornecedor: fornecedorSelecionado,
        nome: this.formulario_produto.get('nome')?.value,
        descricao: this.formulario_produto.get('descricao')?.value,
        valor: this.formulario_produto.get('valor')?.value,
        codigo: 0
      };

      this.produtoService.cadastrarProduto(produto).subscribe(
        (result) => {
          alert('Produto inserido com sucesso.');
          this.router.navigate(['/home']);
        },
        (error) => {
          console.error('Erro ao cadastrar produto:', error);
          // Lide com o erro de acordo com a sua necessidade
        }
      );
    } else {
      console.error('Fornecedor não selecionado');
    }
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Document</title>
    <link rel="stylesheet" href="produtos.component.css">
</head>
<body class="container">
    <div class="container p-5">
        <div class="row">
            <div class="col-6 border border-light rounded p-5 shadow" >

                <form [formGroup]="formulario_produto" *ngIf="formulario_produto" (ngSubmit)="salvarProduto()">
                    <div class="form-group">
                        <label>Fornecedor</label>
                        <select class="form-control form-control-sm" formControlName="fornecedor">
                            <option *ngFor="let fornecedor of fornecedores " [value]="fornecedor.cnpj">
                              {{ fornecedor.nome }}
                            </option>
                          </select>


                    </div>

                    <div class="form-group">
                        <label>Nome</label>
                        <input type="text" class="form-control form-control-sm" formControlName="nome"/>
                    </div>
                    <div class="form-group">
                        <label>Descrição</label>
                        <input type="text" class="form-control form-control-sm" formControlName="descricao"/>
                    </div>
                    <div class="form-group">
                        <label>Valor</label>
                        <input type="text" class="form-control form-control-sm" formControlName="valor"/>
                    </div>    
                    <button type="submit" class="btn btn-primary">Enviar</button>
                </form>
            </div>
        </div>
    </div>
</body>
</html>
