import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Fornecedor } from 'src/app/Model/Fornecedor';
import { Produto } from 'src/app/Model/Produto';
import { FornecedoresService } from 'src/app/Servico/FornecedorServico/fornecedores.service';
import { Router } from '@angular/router';
import { ProdutosService } from 'src/app/Servico/ProdutoServico/produtos.service';

@Component({
  selector: 'app-produtos',
  templateUrl: './produtos.component.html',
  styleUrls: ['./produtos.component.css'],
})
export class ProdutosComponent implements OnInit {
  formulario_produto: FormGroup;
  tituloFormulario: string = '';
  fornecedores: Array<Fornecedor> | undefined;

  constructor(
    private produtoService: ProdutosService,
    private fornecedorService: FornecedoresService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.tituloFormulario = 'Novo Produto';

    this.formulario_produto = this.createForm();

    this.fornecedorService.listar().subscribe((fornecedores) => {
      this.fornecedores = fornecedores;
      if (this.fornecedores && this.fornecedores.length > 0) {
        this.formulario_produto.patchValue({
          fornecedorCNPJ: this.fornecedores[0].cnpj,
        });
      }
    });
  }

  createForm(): FormGroup {
    return new FormGroup({
      fornecedorCNPJ: new FormControl(null, Validators.required),
      nome: new FormControl(null, Validators.required),
      descricao: new FormControl(null, Validators.required),
      valor: new FormControl(null, Validators.required),
    });
  }

  enviarFormulario(): void {
    // Verifica se o formulário é válido antes de enviar a solicitação
    if (this.formulario_produto.valid) {
      const produto: Produto = this.formulario_produto.value;
      console.log('Dados do produto:', produto);

      this.produtoService.cadastrar(produto).subscribe(
        (result) => {
          alert('Produto inserido com sucesso.');
          this.router.navigate(['/home']);
        },
        (error) => {
          console.error('Erro no cadastro do produto:', error);
          alert('Erro ao cadastrar o produto. Verifique os dados e tente novamente.');
        }
      );
    } else {
      alert('Por favor, preencha todos os campos obrigatórios.');
    }
  }
}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Document</title>
    <link rel="stylesheet" href="produtos.component.css" />
  </head>
  <body class="container">
    <div class="container p-5">
      <div class="row">
        <div class="col-6 border border-light rounded p-5 shadow">
          <h6>{{ tituloFormulario }}</h6>
          <form
            [formGroup]="formulario_produto"
            *ngIf="formulario_produto"
            (ngSubmit)="enviarFormulario()"
          >
            <div class="form-group">
              <label>Fornecedor</label>
              <select
                class="form-control form-control-sm"
                formControlName="fornecedorCNPJ"
              >
                <option
                  *ngFor="let fornecedor of fornecedores"
                  [value]="fornecedor.cnpj"
                >
                  {{ fornecedor.nome }}
                </option>
              </select>
            </div>

            <div class="form-group">
              <label>Nome</label>
              <input
                type="text"
                class="form-control form-control-sm"
                formControlName="nome"
              />
            </div>
            <div class="form-group">
              <label>Descrição</label>
              <input
                type="text"
                class="form-control form-control-sm"
                formControlName="descricao"
              />
            </div>
            <div class="form-group">
              <label>Valor</label>
              <input
                type="text"
                class="form-control form-control-sm"
                formControlName="valor"
              />
            </div>
            <button type="submit" class="btn btn-primary">Enviar</button>
          </form>
        </div>
      </div>
    </div>
  </body>
</html>
