import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { ProdutosService } from 'src/app/ProdutoServico/produtos.service';
import { Produto } from 'src/app/Model/Produto';
import { Router } from '@angular/router';
import {FOr}

@Component({
  selector: 'app-produtos',
  templateUrl: "./produtos.component.html",
  styleUrls: ['./produtos.component.css']
})
export class ProdutosComponent implements OnInit {
  formulario_produto: any;
  tituloFormulario: string = '';  
  constructor(private produtoService : ProdutosService, private router : Router) { }

  ngOnInit(): void {
  this.tituloFormulario = 'Novo Produto';
  this.formulario_produto= new FormGroup({
  fornecedor: new FormControl(null),
  nome: new FormControl(null),
  descricao: new FormControl(null),
  valor: new FormControl(null)
  })
  }

  cadastrarProduto(): void {
  const produto : Produto = this.formulario_produto.value;
  this.produtoService.cadastrar(produto).subscribe(
    result => {
      alert('Produto inserido com sucesso.');
      this.router.navigate(["/home"]);
    },
    error => {
      console.error('Erro ao cadastrar o produto:', error);
      // Adicione tratamento de erro adequado conforme necess√°rio
  })
  }
  }
