import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { ClientesService } from 'src/app/clientes.service';
import { Cliente } from 'src/app/Cliente';
import { Router } from '@angular/router';
@Component({
  selector: 'app-clientes',
  templateUrl: './clientes.component.html',
  styleUrls: ['./clientes.component.css']
})
export class ClientesComponent implements OnInit {
  formulario: any;
  tituloFormulario: string = '';
  constructor(private clientesService : ClientesService, private router : Router) { }
  ngOnInit(): void {
  this.tituloFormulario = 'Novo Cliente';
  this.formulario = new FormGroup({
  cpf: new FormControl(null),
  nome: new FormControl(null),
  email: new FormControl(null),
  idade: new FormControl(null)
  })
  }

  enviarFormulario(): void {
  const cliente : Cliente = this.formulario.value;
  this.clientesService.cadastrar(cliente).subscribe(result => {
  alert('Cliente inserido com sucesso.');
  this.router.navigate(["/home"]);
  })
  }

  obterClientes(): void{
    this.clientesService.listar().subscribe((clientes) =>{
      this.clientes = clientes
    });

  }
  }
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Document</title>
    <link rel="stylesheet" href="clientes.component.css">
</head>
<body>

    <div class="container-xl mx-auto align-content-xl-center justify-content-center justify-content-md-around">
        <div class="row">
            <div class="col-6 border border-light rounded p-5 shadow mx-auto" *ngIf="formulario">
                <h6>{{ tituloFormulario }}</h6>
                <form [formGroup]="formulario" (ngSubmit)="enviarFormulario()">
                    <div class="form-group">
                        <label>Cpf</label>
                        <input type="text" class="form-control form-control-sm" formControlName="cpf"/>
                    </div>
                    <div class="form-group">
                        <label>Nome</label>
                        <input type="text" class="form-control form-control-sm" formControlName="nome"/>
                    </div>
                    <div class="form-group">
                        <label>Email</label>
                        <input type="text" class="form-control form-control-sm" formControlName="email"/>
                    </div>
                    <div class="form-group">
                        <label>Idade</label>
                        <input type="text" class="form-control form-control-sm" formControlName="idade"/>
                    </div>

                    <div class="container">
                        <div class="row">
                            <div class="col">
                                <button type="submit" class="btn btn-sm btn-outline-secondary">Salvar</button>
                            </div>
                            <div class="col">
                                <button type="button" class="btn btn-sm btn-light">Voltar</button>
                            </div>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    </div>

</body>
</html>
