import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Fornecedor } from 'src/app/Model/Fornecedor'; 
import { Produto } from 'src/app/Model/Produto'; 
import { FornecedoresService } from 'src/app/Servico/FornecedorServico/fornecedores.service'; 
import { Router } from '@angular/router';
import { ProdutosService } from 'src/app/Servico/ProdutoServico/produtos.service';

@Component({
  selector: 'app-produtos',
  templateUrl: './produtos.component.html',
  styleUrls: ['./produtos.component.css'],
})
// ... (seu código existente)

export class CadastroProdutoComponent implements OnInit {

  formulario_produto!: FormGroup;
  fornecedores!: Fornecedor[];

  constructor(
    private fb: FormBuilder,
    private fornecedorService: FornecedoresService, // Substitua pelo seu serviço real
    private produtoService: ProdutosService      
  ) { }

  ngOnInit(): void {
    // Inicialize o formulário reativo
    this.formulario_produto = this.fb.group({
      fornecedor: ['', Validators.required],
      nome: ['', Validators.required],
      descricao: ['', Validators.required],
      valor: ['', Validators.required]
    });

    // Obtenha a lista de fornecedores do seu serviço
    this.fornecedorService.listar().subscribe(
      (fornecedores: Fornecedor[]) => {
        this.fornecedores = fornecedores;
      },
      error => {
        console.error('Erro ao obter fornecedores:', error);
      });
  }


  // Método para salvar o produto
  salvarProduto() {
    if (this.formulario_produto.valid) {
      const produto = this.formulario_produto.value;

      // Chame o serviço para salvar o produto
      this.produtoService.cadastrarProduto(produto).subscribe(
        (produtoSalvo) => {
          console.log('Produto salvo com sucesso:', produtoSalvo);
          // Adicione aqui qualquer lógica adicional que você queira executar após o salvamento.
        },
        (error) => {
          console.error('Erro ao salvar o produto:', error);
        }
      );
      }
    }
    }
