import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { ProdutosService } from 'src/app/Servico/ProdutoServico/produtos.service';
import { Produto } from 'src/app/Model/Produto';
import { Router } from '@angular/router';
import { Fornecedor } from 'src/app/Model/Fornecedor';
import { FornecedoresService } from 'src/app/Servico/FornecedorServico/fornecedores.service';

@Component({
  selector: 'app-produtos',
  templateUrl: "./produtos.component.html",
  styleUrls: ['./produtos.component.css']
})
export class ProdutosComponent implements OnInit {
  formulario_produto: any;
  tituloFormulario: string = '';  
  fornecedor: Array<Fornecedor> | undefined;

  constructor(private produtoService : ProdutosService,
     private router : Router,
     private fornecedorService: FornecedoresService) { }

  ngOnInit(): void {
  this.tituloFormulario = 'Novo Produto';
  this.fornecedorService.listar().subscribe(fornecedor => {
    this.fornecedor = fornecedor;
    if (this.fornecedor && this.fornecedor.length > 0) {
      this.formulario_produto.get('fornecedor')?.setValue(this.fornecedor[0].cnpj);
    }
  });
  this.formulario_produto= new FormGroup({
  fornecedor: new FormControl(null),
  nome: new FormControl(null),
  descricao: new FormControl(null),
  valor: new FormControl(null)
  })
  }

  cadastrarProduto(): void {
  const produto : Produto = this.formulario_produto.value;
  this.produtoService.cadastrar(produto).subscribe(
    result => {
      alert('Produto inserido com sucesso.');
      this.router.navigate(["/home"]);
    },
    error => {
      console.error('Erro ao cadastrar o produto:', error);
      // Adicione tratamento de erro adequado conforme necessário
  })
  }
  }
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Document</title>
    <link rel="stylesheet" href="produtos.component.css">
</head>
<body class="container">
    <div class="container p-5">
        <div class="row">
            <div class="col-6 border border-light rounded p-5 shadow" *ngIf="formulario_produto">
                <h6>{{ tituloFormulario }}</h6>
                <form [formGroup]="formulario_produto" (ngSubmit)="enviarFormulario()">
                    <div class="form-group">
                        <label>Id</label>
                        <input type="text" class="form-control form-control-sm" formControlName="id"/>
                    </div>
                    <div class="form-group">
                        <label>Fornecedor</label>
                        <select class="form-control form-control-sm" formControlName="marcaId">
                            <option *ngFor="let  of " [value]="fornecedor.cnpj">
                                {{ fornecedor.nome }}
                            </option>
                        </select>
                    </div>
                    <div class="form-group">
                        <label>Nome</label>
                        <input type="text" class="form-control form-control-sm" formControlName="nome"/>
                    </div>
                </form>
            </div>
        </div>
    </div>
</body>
</html>
